import React from 'react'
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import '../styles/SliderImg.css';
import { Sliderdata } from '../SliderData'

export const SliderImg = () => {
    const settings = {
        dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 2.5,
      slidesToScroll: 2,
      initialSlide: 0,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 1.7,
            slidesToScroll: 1.7,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
      };
  return (
    <div >
        <Slider {...settings}>
            {Sliderdata.map((item) => (
                <>
                    <span>
                        <a href={item.p2a}>
                            <div style={{backgroundColor:`${item.bg}`}} className='img-div'>
                                <div className='img-css'>
                                    <img src={item.url} alt="" />
                                </div>
                                <div style={{color:`${item.color}`}} className='txt-div'>
                                    <h3>{item.head}</h3>
                                    <p>{item.p1}</p>
                                    <h2>{item.p2}</h2>
                                </div>
                            </div>
                        </a>
                    </span>
                </>
            ))}
        </Slider>
    </div>
  )
}
