import React from 'react'
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import { Itemsdata } from '../Items'

export const ItemsSlider = () => {
    const settings = {
        dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 4,
      initialSlide: 0,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
      };
  return (
    <div>
        <Slider {...settings}>
            {Itemsdata((item) => {
                <>
                    <span>
                        <a href={item.p1a}>
                            <div>
                                <div>
                                    <img src={item.url} alt="" />
                                </div>
                                <div>
                                    <h2>{item.head}</h2>
                                    <p>{item.p1}</p>
                                </div>
                            </div>
                        </a>
                    </span>
                </>
            })}
        </Slider>
    </div>
  )
}
